mapper.select:
SELECT a.*,c.NM_T as C_NM_T,c.PH_P as C_PH_P
FROM accnt a left join  cont c  on a.CONT_ID=c.ID 

	 AND a.ID =#{id} 
	 AND a.ROW_ID =#{row_id} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(a.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND a.UP_DT =#{up_dt} 
	 AND a.C_ID =#{c_id} 
	 AND a.NM_T LIKE #{nm_t} 
	 AND a.XZ_LV =#{xz_lv} 
	 AND a.WZ_T =#{wz_t} 
	 AND a.HY_LV =#{hy_lv} 
	 AND a.TY_LV LIKE #{ty_lv} 
	 AND a.LV_LV =#{lv_lv} 
	 AND a.ST_LV =#{st_lv} 
	 AND a.CM_TX =#{cm_tx} 
	 AND a.WORK_P =#{work_p} 
	 AND a.EMAIL =#{email} 
	 AND a.STATE =#{state} 
	 AND a.CITY =#{city} 
	 AND a.COUNTY =#{county} 
	 AND a.STREET =#{street} 
	 AND a.POSTAL =#{postal} 
	 AND a.BU_ID =#{bu_id} 
	 AND a.CONT_ID =#{cont_id} 
	 AND c.NM_T LIKE #{c_nm_t} 
	 AND c.PH_P LIKE #{c_ph_p} 

ORDER BY a.ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(a.ID) 
FROM accnt a left join  cont c  on a.CONT_ID=c.ID

	 AND a.ID =#{id} 
	 AND a.ROW_ID =#{row_id} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(a.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND a.UP_DT =#{up_dt} 
	 AND a.C_ID =#{c_id} 
	 AND a.NM_T LIKE #{nm_t} 
	 AND a.XZ_LV =#{xz_lv} 
	 AND a.WZ_T =#{wz_t} 
	 AND a.HY_LV =#{hy_lv} 
	 AND a.TY_LV LIKE #{ty_lv} 
	 AND a.LV_LV =#{lv_lv} 
	 AND a.ST_LV =#{st_lv} 
	 AND a.CM_TX =#{cm_tx} 
	 AND a.WORK_P =#{work_p} 
	 AND a.EMAIL =#{email} 
	 AND a.STATE =#{state} 
	 AND a.CITY =#{city} 
	 AND a.COUNTY =#{county} 
	 AND a.STREET =#{street} 
	 AND a.POSTAL =#{postal} 
	 AND a.BU_ID =#{bu_id} 
	 AND a.CONT_ID =#{cont_id} 
	 AND c.NM_T LIKE #{c_nm_t} 
	 AND c.PH_P LIKE #{c_ph_p}

mapper.update:
UPDATE accnt
ID=#{id}, ROW_ID=#{row_id}, C_DT=#{c_dt}, UP_DT=#{up_dt}, C_ID=#{c_id}, NM_T=#{nm_t}, XZ_LV=#{xz_lv}, WZ_T=#{wz_t}, HY_LV=#{hy_lv}, TY_LV=#{ty_lv}, LV_LV=#{lv_lv}, ST_LV=#{st_lv}, CM_TX=#{cm_tx}, WORK_P=#{work_p}, EMAIL=#{email}, STATE=#{state}, CITY=#{city}, COUNTY=#{county}, STREET=#{street}, POSTAL=#{postal}, BU_ID=#{bu_id}, CONT_ID=#{cont_id},
WHERE ID=#{id}
