mapper.select:
SELECT *
FROM asset

	 AND ID =#{id} 
	 AND ROW_ID =#{row_id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND C_ID =#{c_id} 
	 AND BU_ID =#{bu_id} 
	 AND NM_T =#{nm_t} 
	 AND CODE =#{code} 
	 AND XH_T =#{xh_t} 
	 AND CJ_ID =#{cj_id} 
	 AND unix_timestamp(E_DT) >=unix_timestamp(#{e_dtFrom}) 
	 AND unix_timestamp(E_DT) <=unix_timestamp(#{e_dtTo}) 
	 AND E_DT =#{e_dt} 
	 AND ST_LV =#{st_lv} 
	 AND FZ_ID =#{fz_id} 
	 AND JD_T =#{jd_t} 
	 AND unix_timestamp(GM_DT) >=unix_timestamp(#{gm_dtFrom}) 
	 AND unix_timestamp(GM_DT) <=unix_timestamp(#{gm_dtTo}) 
	 AND GM_DT =#{gm_dt} 
	 AND S_DT =#{s_dt} 
	 AND SC_DT =#{sc_dt} 
	 AND ZQ_N =#{zq_n} 
	 AND YH_DT =#{yh_dt} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(ID) 
FROM asset 

	 AND ID =#{id} 
	 AND ROW_ID =#{row_id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND C_ID =#{c_id} 
	 AND BU_ID =#{bu_id} 
	 AND NM_T =#{nm_t} 
	 AND CODE =#{code} 
	 AND XH_T =#{xh_t} 
	 AND CJ_ID =#{cj_id} 
	 AND unix_timestamp(E_DT) >=unix_timestamp(#{e_dtFrom}) 
	 AND unix_timestamp(E_DT) <=unix_timestamp(#{e_dtTo}) 
	 AND E_DT =#{e_dt} 
	 AND ST_LV =#{st_lv} 
	 AND FZ_ID =#{fz_id} 
	 AND JD_T =#{jd_t} 
	 AND unix_timestamp(GM_DT) >=unix_timestamp(#{gm_dtFrom}) 
	 AND unix_timestamp(GM_DT) <=unix_timestamp(#{gm_dtTo}) 
	 AND GM_DT =#{gm_dt} 
	 AND S_DT =#{s_dt} 
	 AND SC_DT =#{sc_dt} 
	 AND ZQ_N =#{zq_n} 
	 AND YH_DT =#{yh_dt}

mapper.update:
UPDATE asset
ID=#{id}, ROW_ID=#{row_id}, C_DT=#{c_dt}, UP_DT=#{up_dt}, C_ID=#{c_id}, BU_ID=#{bu_id}, NM_T=#{nm_t}, CODE=#{code}, XH_T=#{xh_t}, CJ_ID=#{cj_id}, E_DT=#{e_dt}, ST_LV=#{st_lv}, FZ_ID=#{fz_id}, JD_T=#{jd_t}, GM_DT=#{gm_dt}, S_DT=#{s_dt}, SC_DT=#{sc_dt}, ZQ_N=#{zq_n}, YH_DT=#{yh_dt},
WHERE ID=#{id}
