mapper.select:
SELECT a.* ,c.ID as u_id,c.NAME as u_name 
FROM auth_group a 
LEFT JOIN auth_group_access b ON a.id=b.group_id 
LEFT JOIN members c ON b.uid=c.id 

	 AND a.ID =#{id} 
	 AND a.TITLE =#{title} 
	 AND a.STATUS =#{status} 
	 AND a.RULES =#{rules} 
	 AND a.RESP =#{resp} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(a.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND a.UP_DT =#{up_dt} 
	 AND a.BU_ID =#{bu_id} 

ORDER BY a.ID DESC

SELECT a.* 
FROM auth_group a  

	 AND a.ID =#{id} 
	 AND a.TITLE =#{title} 
	 AND a.STATUS =#{status} 
	 AND a.RULES =#{rules} 
	 AND a.RESP =#{resp} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(a.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND a.UP_DT =#{up_dt} 
	 AND a.BU_ID =#{bu_id} 

ORDER BY a.ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM auth_group 

	 AND ID =#{id} 
	 AND TITLE =#{title} 
	 AND STATUS =#{status} 
	 AND RULES =#{rules} 
	 AND RESP =#{resp} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND BU_ID =#{bu_id}

mapper.update:
UPDATE auth_group
ID=#{id}, TITLE=#{title}, STATUS=#{status}, RULES=#{rules}, RESP=#{resp}, C_DT=#{c_dt}, UP_DT=#{up_dt}, BU_ID=#{bu_id},
WHERE ID=#{id}
