mapper.select:
SELECT *
FROM auth_group_access

	 AND ID =#{id} 
	 AND UID =#{uid} 
	 AND GROUP_ID =#{group_id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM auth_group_access 

	 AND ID =#{id} 
	 AND UID =#{uid} 
	 AND GROUP_ID =#{group_id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt}

mapper.update:
UPDATE auth_group_access
ID=#{id}, UID=#{uid}, GROUP_ID=#{group_id}, C_DT=#{c_dt}, UP_DT=#{up_dt},
WHERE ID=#{id}

UPDATE auth_group_access
ID=#{id}, UID=#{uid}, GROUP_ID=#{group_id}, C_DT=#{c_dt}, UP_DT=#{up_dt},
WHERE UID=#{uid}
