mapper.select:
SELECT *
FROM auth_rule

	 AND ID =#{id} 
	 AND NAME =#{name} 
	 AND TITLE =#{title} 
	 AND STATUS =#{status} 
	 AND CONDITION =#{condition} 
	 AND SORT =#{sort} 
	 AND PARENTID =#{parentid} 
	 AND TYPE =#{type} 
	 AND URL =#{url} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM auth_rule 

	 AND ID =#{id} 
	 AND NAME =#{name} 
	 AND TITLE =#{title} 
	 AND STATUS =#{status} 
	 AND CONDITION =#{condition} 
	 AND SORT =#{sort} 
	 AND PARENTID =#{parentid} 
	 AND TYPE =#{type} 
	 AND URL =#{url} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt}

mapper.update:
UPDATE auth_rule
ID=#{id}, NAME=#{name}, TITLE=#{title}, STATUS=#{status}, CONDITION=#{condition}, SORT=#{sort}, PARENTID=#{parentid}, TYPE=#{type}, URL=#{url}, C_DT=#{c_dt}, UP_DT=#{up_dt},
WHERE ID=#{id}
