mapper.select:
SELECT *
FROM cont

	 AND ID =#{id} 
	 AND ROW_ID =#{row_id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND C_ID =#{c_id} 
	 AND NM_T =#{nm_t} 
	 AND JOB =#{job} 
	 AND SEX =#{sex} 
	 AND PH_P =#{ph_p} 
	 AND QQ =#{qq} 
	 AND PID =#{pid} 
	 AND USER_ID =#{user_id} 
	 AND BU_ID =#{bu_id} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(ID) 
FROM cont 

	 AND ID =#{id} 
	 AND ROW_ID =#{row_id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND C_ID =#{c_id} 
	 AND NM_T =#{nm_t} 
	 AND JOB =#{job} 
	 AND SEX =#{sex} 
	 AND PH_P =#{ph_p} 
	 AND QQ =#{qq} 
	 AND PID =#{pid} 
	 AND USER_ID =#{user_id} 
	 AND BU_ID =#{bu_id}

mapper.update:
UPDATE cont
ID=#{id}, ROW_ID=#{row_id}, C_DT=#{c_dt}, UP_DT=#{up_dt}, C_ID=#{c_id}, NM_T=#{nm_t}, JOB=#{job}, SEX=#{sex}, PH_P=#{ph_p}, QQ=#{qq}, PID=#{pid}, USER_ID=#{user_id}, BU_ID=#{bu_id},
WHERE ID=#{id}
