mapper.select:
SELECT a.* ,b.nm_t as b_nm_t 
FROM departments a 
LEFT JOIN bu b on a.bu_id=b.id 

	 AND a.ID =#{id} 
	 AND a.ROW_ID =#{row_id} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(a.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND a.UP_DT =#{up_dt} 
	 AND a.C_ID =#{c_id} 
	 AND a.NM_T =#{nm_t} 
	 AND a.STATE =#{state} 
	 AND a.CITY =#{city} 
	 AND a.COUNTY =#{county} 
	 AND a.STREET =#{street} 
	 AND a.STATUS =#{status} 
	 AND a.BU_ID =#{bu_id} 
	 AND a.CZ_NUM =#{cz_num} 
	 AND a.PH_NUM =#{ph_num} 

ORDER BY a.ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM departments 

	 AND ID =#{id} 
	 AND ROW_ID =#{row_id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND C_ID =#{c_id} 
	 AND NM_T =#{nm_t} 
	 AND STATE =#{state} 
	 AND CITY =#{city} 
	 AND COUNTY =#{county} 
	 AND STREET =#{street} 
	 AND STATUS =#{status} 
	 AND BU_ID =#{bu_id} 
	 AND CZ_NUM =#{cz_num} 
	 AND PH_NUM =#{ph_num}

mapper.update:
UPDATE departments
ID=#{id}, ROW_ID=#{row_id}, C_DT=#{c_dt}, UP_DT=#{up_dt}, C_ID=#{c_id}, NM_T=#{nm_t}, STATE=#{state}, CITY=#{city}, COUNTY=#{county}, STREET=#{street}, STATUS=#{status}, BU_ID=#{bu_id}, CZ_NUM=#{cz_num}, PH_NUM=#{ph_num},
WHERE ID=#{id}
