mapper.select:
SELECT m.*,aga.group_id as group_id FROM members m left join auth_group_access aga on
m.id=aga.uid 

	 AND m.ID =#{id} 
	 AND m.ROW_ID =#{row_id} 
	 AND m.USERNAME =#{username} 
	 AND m.USERPWD =#{userpwd} 
	 AND m.RESP =#{resp} 
	 AND m.STATUS =#{status} 
	 AND unix_timestamp(m.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(m.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND m.C_DT =#{c_dt} 
	 AND unix_timestamp(m.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(m.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND m.UP_DT =#{up_dt} 
	 AND m.TYPE =#{type} 
	 AND m.BU_ID =#{bu_id} 
	 AND m.IMG =#{img} 
	 AND m.SIGN =#{sign} 
	 AND m.NAME =#{name} 
	 AND m.SIGN_FLG =#{sign_flg} 

ORDER BY m.ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(ID) 
FROM members 

	 AND ID =#{id} 
	 AND ROW_ID =#{row_id} 
	 AND USERNAME =#{username} 
	 AND USERPWD =#{userpwd} 
	 AND RESP =#{resp} 
	 AND STATUS =#{status} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND TYPE =#{type} 
	 AND BU_ID =#{bu_id} 
	 AND IMG =#{img} 
	 AND SIGN =#{sign} 
	 AND NAME =#{name} 
	 AND m.SIGN_FLG =#{sign_flg}

mapper.update:
UPDATE members
ID=#{id}, ROW_ID=#{row_id}, USERNAME=#{username}, USERPWD=#{userpwd}, RESP=#{resp}, STATUS=#{status}, C_DT=#{c_dt}, UP_DT=#{up_dt}, TYPE=#{type}, BU_ID=#{bu_id}, IMG=#{img}, SIGN=#{sign}, NAME=#{name}, SIGN_FLG=#{sign_flg},
WHERE ID=#{id}
