mapper.select:
SELECT m.*,aga.group_id as group_id ,c.nm_t as c_nm_t ,d.nm_t as a_nm_t,
e.nm_t as b_nm_t ,f.nm_t as d_nm_t
FROM members m 
left join auth_group_access aga on m.id=aga.uid  
LEFT JOIN cont c on m.id=c.user_id  
LEFT JOIN accnt d on d.id=c.pid  
LEFT JOIN bu e on e.id=m.bu_id   
LEFT JOIN departments f on f.id=m.departments_id   


	 AND m.ID =#{id} 
	 AND m.ROW_ID =#{row_id} 
	 AND m.USERNAME =#{username} 
	 AND m.USERPWD =#{userpwd} 
	 AND m.RESP =#{resp} 
	 AND m.STATUS =#{status} 
	 AND unix_timestamp(m.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(m.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND m.C_DT =#{c_dt} 
	 AND unix_timestamp(m.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(m.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND m.UP_DT =#{up_dt} 
	 AND m.TYPE =#{type} 
	 AND m.TYPE <> #{typeno} 
	 AND m.BU_ID =#{bu_id} 
	 AND m.IMG =#{img} 
	 AND m.SIGN =#{sign} 
	 AND m.NAME =#{name} 
	 AND m.SIGN_FLG =#{sign_flg} 
	 AND m.ACCNT_ID =#{accnt_id} 
	 AND m.ACCNT_ID IS NULL 
	 AND m.LAST_NAME =#{last_name} 
	 AND m.DEPARTMENTS_ID =#{departments_id} 

ORDER BY m.ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(ID) 
FROM members 

	 AND ID =#{id} 
	 AND ROW_ID =#{row_id} 
	 AND USERNAME =#{username} 
	 AND USERPWD =#{userpwd} 
	 AND RESP =#{resp} 
	 AND STATUS =#{status} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND TYPE =#{type} 
	 AND m.TYPE <> #{typeno} 
	 AND BU_ID =#{bu_id} 
	 AND IMG =#{img} 
	 AND SIGN =#{sign} 
	 AND NAME =#{name} 
	 AND m.SIGN_FLG =#{sign_flg} 
	 AND m.ACCNT_ID =#{accnt_id} 
	 AND m.ACCNT_ID IS NULL 
	 AND m.LAST_NAME =#{last_name} 
	 AND m.DEPARTMENTS_ID =#{departments_id}

mapper.update:
UPDATE members
ID=#{id}, ROW_ID=#{row_id}, USERNAME=#{username}, USERPWD=#{userpwd}, RESP=#{resp}, STATUS=#{status}, C_DT=#{c_dt}, UP_DT=#{up_dt}, TYPE=#{type}, BU_ID=#{bu_id}, IMG=#{img}, SIGN=#{sign}, NAME=#{name}, SIGN_FLG=#{sign_flg}, ACCNT_ID=#{accnt_id}, LAST_NAME=#{last_name}, DEPARTMENTS_ID=#{departments_id},
WHERE ID=#{id}
