mapper.select:
SELECT *
FROM notices

	 AND ID =#{id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND C_ID =#{c_id} 
	 AND BU_ID =#{bu_id} 
	 AND TITLE =#{title} 
	 AND CONTENTS =#{contents} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(ID) 
FROM notices 

	 AND ID =#{id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND C_ID =#{c_id} 
	 AND BU_ID =#{bu_id} 
	 AND TITLE =#{title} 
	 AND CONTENTS =#{contents}

mapper.update:
UPDATE notices
ID=#{id}, C_DT=#{c_dt}, UP_DT=#{up_dt}, C_ID=#{c_id}, BU_ID=#{bu_id}, TITLE=#{title}, CONTENTS=#{contents},
WHERE ID=#{id}
