mapper.select:
    SELECT p.*, a1.NM_T as A_E, a2.NM_T as A_SV 
	FROM pact p left join  accnt a1 on p.PID=a1.ID  left join accnt a2 on p.JL_ID=a2.ID 
	WHERE p.ID=#{id}

SELECT p.*, a1.NM_T as A_E, a2.NM_T as A_SV 
FROM pact p left join  accnt a1 on p.PID=a1.ID  left join accnt a2 on p.JL_ID=a2.ID 

	 AND p.ID =#{id} 
	 AND p.ROW_ID =#{row_id} 
	 AND unix_timestamp(p.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(p.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND p.C_DT =#{c_dt} 
	 AND unix_timestamp(p.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(p.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND p.UP_DT =#{up_dt} 
	 AND p.C_ID =#{c_id} 
	 AND p.PID =#{pid} 
	 AND p.CODE LIKE #{code} 
	 AND p.TY_LV LIKE #{ty_lv} 
	 AND p.NM_T LIKE #{nm_t} 
	 AND p.JL_ID =#{jl_id} 
	 AND p.JL_CONT_ID =#{jl_cont_id} 
	 AND p.ST_LV LIKE #{st_lv} 
	 AND p.QD_DT =#{qd_dt} 
	 AND p.SJWC_DT =#{sjwc_dt} 
	 AND p.SJKS_DT =#{sjks_dt} 
	 AND p.JHWC_DT =#{jhwc_dt} 
	 AND p.FF_DT =#{ff_dt} 
	 AND p.WTFY =#{wtfy} 
	 AND p.DB_ID =#{db_id} 
	 AND p.CONT_ID =#{cont_id} 
	 AND p.BU_ID =#{bu_id} 
	 AND p.LS_T =#{ls_t} 
	 AND p.STATE =#{state} 
	 AND p.CITY =#{city} 
	 AND p.COUNTY =#{county} 
	 AND p.STREET =#{street} 
	 AND p.FLG LIKE #{flg} 
	 AND p.EWM =#{ewm} 
	 AND p.TERMS =#{terms} 
	 AND p.CM_TX =#{cm_tx} 
	 AND p.TJ_F =#{tj_f} 

	 AND a1.NM_T LIKE #{a_e} 
	 AND a2.NM_T LIKE #{a_sv} 

ORDER BY p.ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(p.ID) 
FROM pact p left join  accnt a1 on p.PID=a1.ID  left join accnt a2 on p.JL_ID=a2.ID 

	 AND p.ID =#{id} 
	 AND p.ROW_ID =#{row_id} 
	 AND unix_timestamp(p.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(p.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND p.C_DT =#{c_dt} 
	 AND unix_timestamp(p.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(p.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND p.UP_DT =#{up_dt} 
	 AND p.C_ID =#{c_id} 
	 AND p.PID =#{pid} 
	 AND p.CODE LIKE #{code} 
	 AND p.TY_LV LIKE #{ty_lv} 
	 AND p.NM_T LIKE #{nm_t} 
	 AND p.JL_ID =#{jl_id} 
	 AND p.JL_CONT_ID =#{jl_cont_id} 
	 AND p.ST_LV LIKE  #{st_lv} 
	 AND p.QD_DT =#{qd_dt} 
	 AND p.SJWC_DT =#{sjwc_dt} 
	 AND p.SJKS_DT =#{sjks_dt} 
	 AND p.JHWC_DT =#{jhwc_dt} 
	 AND p.FF_DT =#{ff_dt} 
	 AND p.WTFY =#{wtfy} 
	 AND p.DB_ID =#{db_id} 
	 AND p.CONT_ID =#{cont_id} 
	 AND p.BU_ID =#{bu_id} 
	 AND p.LS_T =#{ls_t} 
	 AND p.STATE =#{state} 
	 AND p.CITY =#{city} 
	 AND p.COUNTY =#{county} 
	 AND p.STREET =#{street} 
	 AND p.FLG LIKE #{flg} 
	 AND p.EWM =#{ewm} 
	 AND p.TERMS =#{terms} 
	 AND p.CM_TX =#{cm_tx} 
	 AND p.TJ_F =#{tj_f} 

	 AND a1.NM_T LIKE #{a_e} 
	 AND a2.NM_T LIKE #{a_sv}

mapper.update:
UPDATE pact
ID=#{id}, ROW_ID=#{row_id}, C_DT=#{c_dt}, UP_DT=#{up_dt}, C_ID=#{c_id}, PID=#{pid}, CODE=#{code}, TY_LV=#{ty_lv}, NM_T=#{nm_t}, JL_ID=#{jl_id}, JL_CONT_ID=#{jl_cont_id}, ST_LV=#{st_lv}, QD_DT=#{qd_dt}, SJWC_DT=#{sjwc_dt}, SJKS_DT=#{sjks_dt}, JHWC_DT=#{jhwc_dt}, FF_DT=#{ff_dt}, WTFY=#{wtfy}, DB_ID=#{db_id}, CONT_ID=#{cont_id}, BU_ID=#{bu_id}, LS_T=#{ls_t}, STATE=#{state}, CITY=#{city}, COUNTY=#{county}, STREET=#{street}, FLG=#{flg}, EWM=#{ewm}, TERMS=#{terms}, CM_TX=#{cm_tx}, TJ_F=#{tj_f},
WHERE ID=#{id}
