mapper.select:
SELECT *
FROM results

	 AND ID =#{id} 
	 AND ROW_ID =#{row_id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND C_ID =#{c_id} 
	 AND PID =#{pid} 
	 AND PROD_ID =#{prod_id} 
	 AND PROD_NAME =#{prod_name} 
	 AND BOOL =#{bool} 
	 AND VALUE =#{value} 
	 AND FLG =#{flg} 
	 AND STATAND_LV =#{statand_lv} 
	 AND STATAND =#{statand} 
	 AND STATAND_VA =#{statand_va} 
	 AND REL_ID =#{rel_id} 
	 AND INBZ_T =#{inbz_t} 
	 AND CM_T =#{cm_t} 
	 AND WD =#{wd} 
	 AND SD =#{sd} 
	 AND BU_ID =#{bu_id} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(ID) 
FROM results 

	 AND ID =#{id} 
	 AND ROW_ID =#{row_id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND C_ID =#{c_id} 
	 AND PID =#{pid} 
	 AND PROD_ID =#{prod_id} 
	 AND PROD_NAME =#{prod_name} 
	 AND BOOL =#{bool} 
	 AND VALUE =#{value} 
	 AND FLG =#{flg} 
	 AND STATAND_LV =#{statand_lv} 
	 AND STATAND =#{statand} 
	 AND STATAND_VA =#{statand_va} 
	 AND REL_ID =#{rel_id} 
	 AND INBZ_T =#{inbz_t} 
	 AND CM_T =#{cm_t} 
	 AND WD =#{wd} 
	 AND SD =#{sd} 
	 AND BU_ID =#{bu_id}

mapper.update:
UPDATE results
ID=#{id}, ROW_ID=#{row_id}, C_DT=#{c_dt}, UP_DT=#{up_dt}, C_ID=#{c_id}, PID=#{pid}, PROD_ID=#{prod_id}, PROD_NAME=#{prod_name}, BOOL=#{bool}, VALUE=#{value}, FLG=#{flg}, STATAND_LV=#{statand_lv}, STATAND=#{statand}, STATAND_VA=#{statand_va}, REL_ID=#{rel_id}, INBZ_T=#{inbz_t}, CM_T=#{cm_t}, WD=#{wd}, SD=#{sd}, BU_ID=#{bu_id},
WHERE ID=#{id}
