mapper.select:
SELECT a.*,b.nm_t,b.cm_tx 
FROM sample_templet a LEFT JOIN  lov b 
ON a.lov_id= b.id 

	 AND a.ID =#{id} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(a.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND a.UP_DT =#{up_dt} 
	 AND a.SHOW_FLG =#{show_flg} 
	 AND a.SORT =#{sort} 
	 AND a.LOV_ID =#{lov_id} 
	 AND a.PROD_ID =#{prod_id} 

ORDER BY a.SORT ASC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(ID) 
FROM sample_templet 

	 AND ID =#{id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND SHOW_FLG =#{show_flg} 
	 AND SORT =#{sort} 
	 AND LOV_ID =#{lov_id} 
	 AND PROD_ID =#{prod_id}

mapper.update:
UPDATE sample_templet
ID=#{id}, C_DT=#{c_dt}, UP_DT=#{up_dt}, SHOW_FLG=#{show_flg}, SORT=#{sort}, LOV_ID=#{lov_id}, PROD_ID=#{prod_id},
WHERE ID=#{id}
