mapper.select:
SELECT a.*,b.group_id as group_id  FROM 
status a LEFT join group_status b 
ON a.id=b.status_id 

	 AND a.ID =#{id} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(a.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND a.UP_DT =#{up_dt} 
	 AND a.CURR_STATUS =#{curr_status} 
	 AND (a.CURR_STATUS IS NULL OR a.CURR_STATUS='')  
	 AND a.STEP_STATUS =#{step_status} 
	 AND a.TYPE =#{type} 
	 AND a.SORT =#{sort} 
	 AND a.FLAG =#{flag} 
	 AND a.C_ID =#{c_id} 
	 AND a.CM_TX =#{cm_tx} 
	 AND b.GROUP_ID =#{group_id} 

ORDER BY a.SORT ASC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(a.id) 
 FROM 
 status a LEFT join group_status b 
ON a.id=b.status_id  

	 AND a.ID =#{id} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(a.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND a.UP_DT =#{up_dt} 
	 AND a.CURR_STATUS =#{curr_status} 
	 AND (a.CURR_STATUS IS NULL OR a.CURR_STATUS='')  
	 AND a.STEP_STATUS =#{step_status} 
	 AND a.TYPE =#{type} 
	 AND a.SORT =#{sort} 
	 AND a.FLAG =#{flag} 
	 AND a.C_ID =#{c_id} 
	 AND a.CM_TX =#{cm_tx} 
	 AND b.GROUP_ID =#{group_id}

mapper.update:
UPDATE status
ID=#{id}, C_DT=#{c_dt}, UP_DT=#{up_dt}, CURR_STATUS=#{curr_status}, STEP_STATUS=#{step_status}, TYPE=#{type}, SORT=#{sort}, FLAG=#{flag}, C_ID=#{c_id}, CM_TX=#{cm_tx},
WHERE ID=#{id}
