mapper.select:
SELECT a.*,  b.nm_t as s_nm_t,c.name as sy_name,d.name as sh_name,
f.nm_t as pact_nm_t,g.nm_t as a_nm_t
FROM test a  
LEFT JOIN sample  b ON a.SAMPLE_ID=b.ID 
LEFT JOIN members c ON a.SY_ID=c.ID 
LEFT JOIN members d ON a.SH_ID=d.ID 
LEFT JOIN entrust e ON a.PID=e.ID 
LEFT JOIN pact    f ON e.PID=f.ID 
LEFT JOIN accnt   g ON f.PID=g.ID  

	 AND a.ID =#{id} 
	 AND a.ROW_ID =#{row_id} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(a.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND a.UP_DT =#{up_dt} 
	 AND a.C_ID =#{c_id} 
	 AND a.PID =#{pid} 
	 AND a.BU_ID =#{bu_id} 
	 AND a.CODE LIKE #{code} 
	 AND a.SY_DT =#{sy_dt} 
	 AND a.DD_T =#{dd_t} 
	 AND a.SAMPLE_ID =#{sample_id} 
	 AND a.JL_T =#{jl_t} 
	 AND a.SY_ID =#{sy_id} 
	 AND a.SYQZ_ID =#{syqz_id} 
	 AND a.SH_ID =#{sh_id} 
	 AND a.SHYJ_T =#{shyj_t} 
	 AND a.SHQZ_ID =#{shqz_id} 
	 AND a.SPQZ_ID =#{spqz_id} 
	 AND a.BG_F =#{bg_f} 
	 AND a.SH_DT =#{sh_dt} 
	 AND a.BG_DT =#{bg_dt} 
	 AND a.E_DT =#{e_dt} 
	 AND a.CM_TX =#{cm_tx} 
	 AND a.STATUS =#{status} 
	 AND a.FQ_FLG =#{fq_flg} 
	 AND a.EWM =#{ewm} 

	 AND b.NM_T LIKE #{s_nm_t} 
	 AND e.NM_T LIKE #{pact_nm_t} 
	 AND g.NM_T LIKE #{a_nm_t} 


ORDER BY a.ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(a.ID) 
FROM test a  
LEFT JOIN sample  b ON a.SAMPLE_ID=b.ID 
LEFT JOIN members c ON a.SY_ID=c.ID 
LEFT JOIN members d ON a.SH_ID=d.ID 
LEFT JOIN entrust e ON a.PID=e.ID 
LEFT JOIN pact    f ON e.PID=f.ID 
LEFT JOIN accnt   g ON f.PID=g.ID  

	 AND a.ID =#{id} 
	 AND a.ROW_ID =#{row_id} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(a.UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND a.UP_DT =#{up_dt} 
	 AND a.C_ID =#{c_id} 
	 AND a.PID =#{pid} 
	 AND a.BU_ID =#{bu_id} 
	 AND a.CODE =#{code} 
	 AND a.SY_DT =#{sy_dt} 
	 AND a.DD_T =#{dd_t} 
	 AND a.SAMPLE_ID =#{sample_id} 
	 AND a.JL_T =#{jl_t} 
	 AND a.SY_ID =#{sy_id} 
	 AND a.SYQZ_ID =#{syqz_id} 
	 AND a.SH_ID =#{sh_id} 
	 AND a.SHYJ_T =#{shyj_t} 
	 AND a.SHQZ_ID =#{shqz_id} 
	 AND a.SPQZ_ID =#{spqz_id} 
	 AND a.BG_F =#{bg_f} 
	 AND a.SH_DT =#{sh_dt} 
	 AND a.BG_DT =#{bg_dt} 
	 AND a.E_DT =#{e_dt} 
	 AND a.CM_TX =#{cm_tx} 
	 AND a.STATUS =#{status} 
	 AND a.FQ_FLG =#{fq_flg} 
	 AND a.EWM =#{ewm} 

	 AND b.NM_T LIKE #{s_nm_t} 
	 AND e.NM_T LIKE #{pact_nm_t} 
	 AND g.NM_T LIKE #{a_nm_t}

mapper.update:
UPDATE test
ID=#{id}, ROW_ID=#{row_id}, C_DT=#{c_dt}, UP_DT=#{up_dt}, C_ID=#{c_id}, PID=#{pid}, BU_ID=#{bu_id}, CODE=#{code}, SY_DT=#{sy_dt}, DD_T=#{dd_t}, SAMPLE_ID=#{sample_id}, JL_T=#{jl_t}, SY_ID=#{sy_id}, SYQZ_ID=#{syqz_id}, SH_ID=#{sh_id}, SHYJ_T=#{shyj_t}, SHQZ_ID=#{shqz_id}, SPQZ_ID=#{spqz_id}, BG_F=#{bg_f}, SH_DT=#{sh_dt}, BG_DT=#{bg_dt}, E_DT=#{e_dt}, CM_TX=#{cm_tx}, STATUS=#{status}, FQ_FLG=#{fq_flg}, EWM=#{ewm},
WHERE ID=#{id}

UPDATE test
ID=#{id}, ROW_ID=#{row_id}, C_DT=#{c_dt}, UP_DT=#{up_dt}, C_ID=#{c_id}, PID=#{pid}, BU_ID=#{bu_id}, CODE=#{code}, SY_DT=#{sy_dt}, DD_T=#{dd_t}, SAMPLE_ID=#{sample_id}, JL_T=#{jl_t}, SY_ID=#{sy_id}, SYQZ_ID=#{syqz_id}, SH_ID=#{sh_id}, SHYJ_T=#{shyj_t}, SHQZ_ID=#{shqz_id}, SPQZ_ID=#{spqz_id}, BG_F=#{bg_f}, SH_DT=#{sh_dt}, BG_DT=#{bg_dt}, E_DT=#{e_dt}, CM_TX=#{cm_tx}, STATUS=#{status}, FQ_FLG=#{fq_flg}, EWM=#{ewm},
WHERE PID=#{pid}
